import React from 'react';
import logo from '../images/logo.svg'

export default function Form() {
    const gerQR =  () => {
    const data = document.querySelector('.data').value

    const qrCodeAPI = `https://api.qrserver.com/v1/create-qr-code/?data=${data}&size=300x200&color=15ff00&bgcolor=fffb00&margin=1&qzone=1&format=png`

  console.log(data)

    document.querySelector('.qrcode').src = qrCodeAPI
  }

  return (
    <div className='App'>
        Input some text or URL: <input type='text' className='data' />
        <br />
        Size (wxh): <input type='text' className='data' />
        <br />
        <button className='btn' onClick={gerQR}>Generate QR Code</button>
        <br />
        <br />
        <img src={logo} className='qrcode' />
    </div>
  );
}
