import React, { useState } from 'react';
import logo from '../images/logo.svg'

export default function Form() {
  const [data, setData] = useState('')
  //const [sizew, setSizew] = useState('')
  //const [sizeh, setSizeh] = useState('')
  /*Size (wxh):
        <input
          type='text'
          className='sizew'
          onChage={e => setSizew(e.target.value)}
        />
        x
        <input
          type='text'
          className='sizeh'
          onChage={e => setSizeh(e.target.value)}
        /> */

  return (
    <div className='App'>
        Input some text or URL:
        <input
          type='text'
          className='data'
          value={e => setData(e.target.value)}
        />
        <br />
        
        <br />
        <button
          className='btn'
          >Generate QR Code
        </button>
        <br />
        <br />
        <img
          src={logo}
          className='qrcode'
        />
    </div>
  );
}
