import React, { useState } from 'react';
import logo from '../images/logo.svg'

export default function Form() {
  const [data, setData] = useState('https://www.w3schools.com/js/default.asp')
  const [sizew, setSizew] = useState('300')
  const [sizeh, setSizeh] = useState('300')

  const gerQR = ()=>{
    const qrCodeAPI = `https://api.qrserver.com/v1/create-qr-code/?data=${data}&size=${sizew}x${sizeh}&color=15ff00&bgcolor=fffb00&margin=1&qzone=1&format=png`
  }

  document.querySelector('.qrcode').src = qrCodeAPI

  return (
    <div className='App'>
        Input some text or URL:
        <input
          type='text'
          className='data'
          onChage={e => setData(e.target.value)}
        />
        <br />
        Size (wxh):
        <input
          type='text'
          className='sizew'
          onChage={e => setSizew(e.target.value)}
        />
        x
        <input
          type='text'
          className='sizeh'
          onChage={e => setSizeh(e.target.value)}
        />
        <br />
        <button
          className='btn'
          onClick={gerQR}>Generate QR Code
        </button>
        <br />
        <br />
        <img
          src={logo}
          className='qrcode'
        />
    </div>
  );
}
