import React, { useState } from 'react';
import logo from '../images/logo.svg'

export default function Form() {
  const [qrcode, setQRcode] = useState('')
    const gerQR =  () => {
      setQRcode(()=>{
        const data = document.querySelector('.data').value
        const sizew = document.querySelector('.sizew').value
        const sizeh = document.querySelector('.sizeh').value

        const qrCodeAPI = `https://api.qrserver.com/v1/create-qr-code/?data=${data}&size=${sizew}x${sizeh}&color=15ff00&bgcolor=fffb00&margin=1&qzone=1&format=png`

        console.log(data)

        document.querySelector('.qrcode').src = qrCodeAPI
      })
    
  }

  return (
    <div className='App'>
        Input some text or URL: <input type='text' className='data' value="https://www.w3schools.com/js/default.asp" />
        <br />
        Size (wxh): <input type='text' className='sizew' /> x <input type='text' className='sizeh' />
        <br />
        <button className='btn' onClick={gerQR}>Generate QR Code</button>
        <br />
        <br />
        <img src={logo} className='qrcode' />
    </div>
  );
}
