import React, { useState } from 'react';
import logo from '../images/logo.svg'

export default function Form() {
  const [getQR, setgetQR] = useState({
    data: '',
    size: '',
    color: '',
    bgcolor: '',
    format: '',

  })

  const getValues = (e) => {
    const {name, value} = e.target;
    setgetQR((prev)=>{
      return {...prev, [name]: value}
    })
  }

  const formSubmit = () => {
    //console.log(getQR)
    //console.log(getQR.data)
    const {data, size, color, bgcolor, format} = getQR

    const qrAPI = `https://api.qrserver.com/v1/create-qr-code/?data=${data}&size=${size}&color=${color}&bgcolor=${bgcolor}&margin=1&qzone=1&format=${format}`
    document.querySelector('.qrcode').src = qrAPI;
  console.log(qrAPI)
  }

  return (
    <div className='App'>
        Input some text or URL:
          <input 
          type='text'
          className='data'
          name='data'
          onChange={getValues}
        />
        <br />
        Size (wxh):
          <input 
          type='text'
          className='size'
          name='size'
          onChange={getValues}
        />
        <br />
        Color:
          <input 
          type='text'
          className='color'
          name='color'
          onChange={getValues}
        />
        <br />
        Background Color:
          <input 
          type='text'
          className='bgcolor'
          name='bgcolor'
          onChange={getValues}
        />
        <br />
        Format:
          <input 
          type='text'
          className='format'
          name='format'
          onChange={getValues}
        />
        <br />
        <br />
        <button
          className='btn'
          onClick={formSubmit}
        >
          Generate QR Code
        </button>
        <br />
        <br />
        <img
          src={logo}
          className='qrcode'
        />
    </div>
  );
}
