import React, { useState } from 'react';
import logo from '../images/logo.svg'

export default function Form() {
  const [getQR, setgetQR] = useState({
    data: '',
    size: '',
    color: '',
    bgcolor: '',
    format: '',

  })

  const getValues = (e) => {
    const {name, value} = e.target;
    setgetQR((prev)=>{
      return {...prev, [name]: value}
    })
  }

  const formSubmit = () => {
    console.log(getQR)
  }

  return (
    <div className='App'>
        Input some text or URL:
        <input
          type='text'
          className='data'
          name='name'
          onChange={getValues}
        />
        <br />
        
        <br />
        <button
          className='btn'
          onClick={formSubmit}
        >
          Generate QR Code
        </button>
        <br />
        <br />
        <img
          src={logo}
          className='qrcode'
        />
    </div>
  );
}
