import React from 'react';
import logo from '../images/logo.svg'

export default function Form() {
  const qrcodeapi = "https://api.qrserver.com/v1/create-qr-code/?data=https://www.google.com/&size=300x300&color=15ff00&bgcolor=fffb00&margin=50&qzone=50&format=png"
  
  function gerQR(){
    document.querySelector('.qrcode').src = qrcodeapi
  }

  return (
    <div className="App">
      <form>
        <input type='text' className='data' />
        <br />
        <button className='btn' onClick={gerQR}>Generate QR Code</button>
        <br />
        <img src={logo} className='qrcode' />
      </form>
    </div>
  );
}
